function DropDownChange(a){this.status_element=a.status_element;this.status_selector=a.status_selector}DropDownChange.prototype.enable=function(){var b=false;var a=this;$(document).delegate(a.status_element,"hover",function(c){if(c.type==="mouseenter"){b=true}else{b=false}});$(document).delegate(a.status_element,"click",function(d){var c=this;$(c).children("span").hide();$(c).children("select").show();$(c).undelegate(a.status_selector).delegate("select",a.status_selector,function(f){var j=$(this);var i=$('option[value="'+j.val()+'"]',this).text();var g=j.val();var h=j.attr("feedid");var k=j.attr("feedurl");$.ajax({type:"POST",url:k,data:{select_val:g,feed_id:h},success:function(){var e=new Status();e.notify("Processing...",1000);j.hide();$(c).children("span").show()}});j.siblings().text(i)});$(document).delegate("body","click",function(f){if(!b){$(c).children("span").show();$(c).children("select").hide()}})})};