jQuery(function(a){a('select[name="date-filter"]').bind("change",function(b){if((a(this).val()=="default")){window.location=window.location.pathname}else{insertParam("date",a(this).val())}});a('select[name="priority-filter"]').bind("change",function(b){if((a(this).val()=="default")){window.location=window.location.pathname}else{insertParam("priority",a(this).val())}});a('select[name="status-filter"]').bind("change",function(b){if((a(this).val()=="default")){window.location=window.location.pathname}else{insertParam("status",a(this).val())}});a('select[name="rating-limit"]').bind("change",function(b){if((a(this).val()=="default")){window.location=window.location.pathname}else{insertParam("rating",a(this).val())}});a('select[name="feedback-limit"]').bind("change",function(b){if((a(this).val()=="default")){window.location=window.location.pathname}else{insertParam("limit",a(this).val())}});a('select[name="category-filter"]').bind("change",function(b){if((a(this).val()=="default")){window.location=window.location.pathname}else{insertParam("category",a(this).val())}});a('select[name="site_choice"]').bind("change",function(c){var b=a(this).val();window.location=(b=="all")?"all":"?site_id="+a(this).val()})});function insertParam(b,c){b=escape(b);c=escape(c);var e=document.location.search.substr(1).split("&");if(e==""){document.location.search="?"+b+"="+c}else{var a=e.length;var d;while(a--){d=e[a].split("=");if(d[0]==b){d[1]=c;e[a]=d.join("=");break}}if(a<0){e[e.length]=[b,c].join("=")}document.location.search=e.join("&")}};