function s36Lightbox(b,f,e){if(jQuery("#lightbox").size()==0){var a=jQuery('<div id="lightbox"></div>');var d=jQuery('<div id="lightbox-shadow"/>');jQuery(d).click(function(g){closeLightbox()});jQuery("body").append(d);jQuery("body").append(a)}jQuery("#lightbox").empty();if(e!=null){jQuery("#lightbox").append(e+"<div id='lightbox-comment' style='color:#fff;position:absolute;top:-20px;right:10px;'>This is a preview only | <span style='cursor:pointer'>close</span></div>")}jQuery("#lightbox-comment").click(function(g){closeLightbox()});var c=Math.round(b/2);jQuery("#lightbox").css("top",jQuery(window).scrollTop()+50+"px");jQuery("#lightbox").css({width:b,height:f,"margin-left":"-"+c+"px"});jQuery("#lightbox").fadeIn("fast");jQuery("#lightbox-shadow").fadeIn("fast")}function closeLightbox(){jQuery("#lightbox").fadeOut("fast",function(){jQuery(this).empty()});jQuery("#lightbox-shadow").fadeOut("fast")};